import React from "react";
import shortid from "shortid";
import './TodoForm.css'
import 'react-responsive-modal/styles.css';
import { Modal } from 'react-responsive-modal';

export default class TodoForm extends React.Component {
  state = {
    title: "",
    description: "",
    status:"",
    open: false
  };

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleSubmit = event => {

    event.preventDefault();
    if (this.state.title !== "") {
      this.props.onSubmit({
      id: shortid.generate(),
      title: this.state.title,
      description: this.state.description,
      status: this.state.status
    });
    this.setState({
      title: "",
      description: "",
      status:""
    });
    }
  };

  onOpenModal = () => {
    this.setState({ open: true });
  };

  onCloseModal = () => {
    this.setState({ open: false });
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <button onClick={this.onOpenModal}>Add</button>
        <Modal className='modal' open={this.state.open} onClose={this.onCloseModal} center>
          <label> TITLE </label>
          <input
            name="title"
            value={this.state.title}
            onChange={this.handleChange}
            placeholder="todo..."
          />

          <label> DESCRIPTION </label>
          <input
            name="description"
            value={this.state.description}
            onChange={this.handleChange}
            placeholder="description..."
          />

          <label> STATUS </label>
          <div className="radio">
          <label>
            <input name='radio' type="radio" value="To Do" checked={true} />
            To Do
          </label>
        </div>
        <div className="radio">
          <label>
            <input name='radio' type="radio" value="in Progress" />
            in Progress
          </label>
        </div>
        <div className="radio">
          <label>
            <input name='radio' type="radio" value="Done" />
            Done
          </label>
        </div>
          <button onClick={this.handleSubmit}>add todo</button>
        </Modal>

      </form>
    );
  }
}
